case class Item(invoiceNo:String, stockCode:String, description:Option[String], quantity:Int, invoiceDate:String, unitPrice:Double, customerID:Option[Int], Country:String)

val parseRow2Item: (String) => Item = (row: String) => {
//complete body
val tokens = row.split(",")
    val description = if (tokens(2).isEmpty)  None else Some(tokens(2))
    val customerId = Try(Some(tokens(6).toInt)).getOrElse(None)
    //InvoiceNo,StockCode,Description,Quantity,InvoiceDate,UnitPrice,CustomerID,Country
    Item(tokens(0),tokens(1), description, tokens(3).toInt, tokens(4),  tokens(5).toDouble, customerId, tokens(7))

}

val itemsRdd = retailRDD.map(parseRow2Item)
itemsRdd.take(3)
itemsRdd.cache

